from typing import Optional, Dict, Any, List, Union, Type, Mapping
from pydantic import BaseModel
import logging

from agno.models.ollama.chat import Ollama
from agno.models.message import Message
from agno.models.response import ModelResponse
from agno.tools.function import Function

logger = logging.getLogger(__name__)


class ResponseAdapter:
    """Generic adapter for handling different response formats"""

    def detect_type(self, obj: Any) -> str:
        """Detect the type of response object"""
        if isinstance(obj, dict):
            return 'dict'
        elif hasattr(obj, '__dict__'):
            return 'pydantic'
        return 'unknown'

    def get_attribute(self, obj: Any, attr: str, default: Any = None) -> Any:
        """Get attribute from object regardless of type"""
        if isinstance(obj, dict):
            return obj.get(attr, default)
        elif hasattr(obj, '__dict__'):
            return getattr(obj, attr, default)
        return default


class GPTOSSOllamaModel(Ollama):
    """
    GPT-OSS Ollama model with complete tool call support.

    Features:
    - Complete tool call system built from scratch for GPT-OSS
    - Works with ANY Agno tool without modification
    - Reliable tool call parsing and execution
    - Multi-turn conversation support with tool results
    - Generic Pydantic integration for structured outputs
    - Comprehensive error handling and recovery
    """

    def __init__(self, *args, debug: bool = False, **kwargs):
        """Initialize GPT-OSS model with complete tool support."""
        super().__init__(*args, **kwargs)
        self.debug = debug
        self.adapter = ResponseAdapter()

        # Storage for current response format for Pydantic validation
        self._current_response_format: Optional[Type[BaseModel]] = None

    def _generate_tool_schemas(self, functions: dict) -> List[Dict[str, Any]]:
        """Generate tool schemas for GPT-OSS from Agno Function objects."""
        if not functions:
            return []

        schemas = []
        for name, func in functions.items():
            try:
                # Handle Agno Function objects
                if hasattr(func, 'name') and hasattr(func, 'description') and hasattr(func, 'parameters'):
                    # This is an Agno Function object - use its schema directly
                    schema = {
                        "name": func.name,
                        "description": func.description or f"Tool: {func.name}",
                        "parameters": func.parameters or {
                            "type": "object",
                            "properties": {},
                            "required": []
                        }
                    }

                    # If parameters are empty, try to extract from entrypoint
                    if (not func.parameters.get('properties') or 
                        len(func.parameters.get('properties', {})) == 0):
                        if func.entrypoint:
                            schema["parameters"] = self._extract_parameters_from_callable(func.entrypoint)

                else:
                    # Fallback for other function types
                    schema = {
                        "name": name,
                        "description": getattr(func, 'description', f"Tool: {name}"),
                        "parameters": self._extract_parameters_from_callable(func)
                    }

                schemas.append(schema)

                if self.debug:
                    logger.debug(f"Generated schema for tool: {name}")
                    logger.debug(f"Schema: {schema}")

            except Exception as e:
                if self.debug:
                    logger.debug(f"Failed to generate schema for {name}: {e}")

        return schemas

    def _extract_parameters_from_callable(self, func: callable) -> Dict[str, Any]:
        """Extract parameters schema from a callable function."""
        parameters = {
            "type": "object",
            "properties": {},
            "required": []
        }

        try:
            import inspect
            sig = inspect.signature(func)
            for param_name, param in sig.parameters.items():
                if param_name not in ['self', 'agent', 'team']:
                    param_schema = {"type": "string"}  # Default type
                    if param.annotation != inspect.Parameter.empty:
                        if param.annotation == int:
                            param_schema["type"] = "integer"
                        elif param.annotation == float:
                            param_schema["type"] = "number"
                        elif param.annotation == bool:
                            param_schema["type"] = "boolean"

                    parameters["properties"][param_name] = param_schema
                    if param.default == inspect.Parameter.empty:
                        parameters["required"].append(param_name)
        except Exception as e:
            if self.debug:
                logger.debug(f"Failed to extract parameters: {e}")

        return parameters

    def _get_yfinance_entrypoints(self) -> dict:
        """Get a mapping of YFinance function names to their actual callable methods."""
        from agno.tools.yfinance import YFinanceTools

        # Create a temporary YFinance tools instance to access the methods
        yf_temp = YFinanceTools(enable_all=True)

        # Map function names to the actual callable methods
        entrypoints = {
            'get_current_stock_price': yf_temp.get_current_stock_price,
            'get_company_info': yf_temp.get_company_info,
            'get_stock_fundamentals': yf_temp.get_stock_fundamentals,
            'get_income_statements': yf_temp.get_income_statements,
            'get_key_financial_ratios': yf_temp.get_key_financial_ratios,
            'get_analyst_recommendations': yf_temp.get_analyst_recommendations,
            'get_company_news': yf_temp.get_company_news,
            'get_technical_indicators': yf_temp.get_technical_indicators,
            'get_historical_stock_prices': yf_temp.get_historical_stock_prices,
        }

        return entrypoints

    def _create_system_prompt_with_tools(self, original_prompt: str, functions: dict) -> str:
        """Create system prompt that teaches GPT-OSS how to use tools."""
        if not functions:
            return original_prompt

        tool_schemas = self._generate_tool_schemas(functions)

        tools_section = "\n\n=== AVAILABLE TOOLS ===\n"
        tools_section += "ONLY use these exact tools. To call a tool, use this EXACT JSON format:\n\n"
        tools_section += '{\n  "tool_calls": [\n    {\n      "name": "EXACT_TOOL_NAME",\n      "arguments": {"param": "value"}\n    }\n  ]\n}\n\n'
        tools_section += "YOUR AVAILABLE TOOLS (use EXACT names):\n"

        for schema in tool_schemas:
            # Simplified, clearer format
            tools_section += f"\n{schema['name']}:\n"
            tools_section += f"  - Use for: {schema['description'].replace('Tool: ', '').replace('Use this function to ', '')}\n"
            if schema['parameters']['properties']:
                required_params = [p for p in schema['parameters']['properties'].keys() if p in schema['parameters']['required']]
                if required_params:
                    tools_section += f"  - Required: {', '.join(required_params)}\n"

        tools_section += "\nCRITICAL RULES:\n"
        tools_section += "- NEVER make up tool names\n"
        tools_section += "- ONLY use the exact tool names listed above\n"
        tools_section += "- NO other tools exist - do not use repo_browser, container, python, or any other tools\n"
        tools_section += "- When you need data, use ONE of the tools listed above\n"

        return original_prompt + tools_section

    def _extract_tool_calls_from_content(self, content: str) -> List[Dict[str, Any]]:
        """Extract tool calls from GPT-OSS response content."""
        import json
        import re

        tool_calls = []

        # Look for JSON blocks containing tool_calls
        json_pattern = r'\{[^{}]*"tool_calls"[^{}]*\[[^\]]*\][^{}]*\}'
        json_matches = re.findall(json_pattern, content, re.DOTALL)

        for match in json_matches:
            try:
                data = json.loads(match)
                if 'tool_calls' in data and isinstance(data['tool_calls'], list):
                    tool_calls.extend(data['tool_calls'])
                    if self.debug:
                        logger.debug(f"Extracted {len(data['tool_calls'])} tool calls from JSON")
            except json.JSONDecodeError:
                continue

        # Also look for simpler patterns
        if not tool_calls:
            # Try to find tool call patterns without full JSON wrapper
            simple_pattern = r'"name":\s*"([^"]+)"[^}]*"arguments":\s*(\{[^}]*\})'
            simple_matches = re.findall(simple_pattern, content, re.DOTALL)

            for name, args_str in simple_matches:
                try:
                    arguments = json.loads(args_str)
                    tool_calls.append({
                        "name": name,
                        "arguments": arguments
                    })
                    if self.debug:
                        logger.debug(f"Extracted tool call: {name}")
                except json.JSONDecodeError:
                    continue

        return tool_calls

    def _execute_tool_calls(self, tool_calls: List[Dict[str, Any]], functions: dict) -> List[Dict[str, Any]]:
        """Execute tool calls and return results."""
        results = []

        for i, tool_call in enumerate(tool_calls):
            tool_name = tool_call.get('name')
            tool_args = tool_call.get('arguments', {})

            if not tool_name:
                results.append({
                    "tool_call": tool_call,
                    "error": "Missing tool name"
                })
                continue

            if tool_name not in functions:
                available_tools = list(functions.keys())
                results.append({
                    "tool_call": tool_call,
                    "error": f"Tool '{tool_name}' not found. Available tools: {available_tools}"
                })
                continue

            try:
                func_obj = functions[tool_name]
                print(f"DEBUG: Executing tool {tool_name} with args: {tool_args}")
                print(f"DEBUG: Function object type: {type(func_obj)}")

                # Handle Agno Function objects directly
                if hasattr(func_obj, 'entrypoint') and callable(func_obj.entrypoint):
                    print(f"DEBUG: Calling entrypoint directly")
                    result = func_obj.entrypoint(**tool_args)
                    results.append({
                        "tool_call": tool_call,
                        "result": result
                    })
                    print(f"DEBUG: Tool {tool_name} executed successfully: {str(result)[:100]}...")

                # Handle Function objects with run method
                elif hasattr(func_obj, 'run') and callable(func_obj.run):
                    print(f"DEBUG: Calling run method")
                    result = func_obj.run(**tool_args)
                    results.append({
                        "tool_call": tool_call,
                        "result": result
                    })
                    print(f"DEBUG: Tool {tool_name} run successfully: {str(result)[:100]}...")

                # Fallback to Agno's function execution system
                else:
                    print(f"DEBUG: Using Agno function execution system")
                    from agno.utils.functions import get_function_call
                    import json

                    # Convert arguments to JSON string if needed
                    args_str = json.dumps(tool_args) if isinstance(tool_args, dict) else str(tool_args)

                    function_call = get_function_call(
                        name=tool_name,
                        arguments=args_str,
                        functions=functions
                    )

                    if function_call and not function_call.error:
                        # Execute the function call using FunctionCall.execute()
                        print(f"DEBUG: About to execute function call: {function_call}")
                        execution_result = function_call.execute()
                        print(f"DEBUG: Execution result: {execution_result}")
                        print(f"DEBUG: Execution result type: {type(execution_result)}")

                        if execution_result and hasattr(execution_result, 'result'):
                            result = execution_result.result
                            print(f"DEBUG: Got result from execution_result.result: {result}")
                        elif execution_result:
                            result = str(execution_result)
                            print(f"DEBUG: Got result from str(execution_result): {result}")
                        else:
                            result = "No result returned"
                            print(f"DEBUG: No result returned from execution")

                        results.append({
                            "tool_call": tool_call,
                            "result": result
                        })
                        print(f"DEBUG: Tool {tool_name} executed via Agno system: {str(result)[:100]}...")
                    else:
                        error_msg = function_call.error if function_call else "Failed to create function call"
                        results.append({
                            "tool_call": tool_call,
                            "error": error_msg
                        })
                        print(f"DEBUG: Tool {tool_name} failed: {error_msg}")

            except Exception as e:
                results.append({
                    "tool_call": tool_call,
                    "error": f"Tool execution error: {str(e)}"
                })
                print(f"DEBUG: Tool execution failed: {e}")
                import traceback
                traceback.print_exc()

        return results

    async def ainvoke(
        self,
        messages: List[Message],
        response_format: Optional[Union[Dict, Type[BaseModel]]] = None,
        tools: Optional[List[Dict[str, Any]]] = None,
        tool_choice: Optional[Union[str, Dict[str, Any]]] = None,
    ) -> Mapping[str, Any]:
        """
        Complete tool call implementation for GPT-OSS.
        Handles the full conversation flow with tool calls.
        """
        # Convert tools to functions format for internal use
        functions = {}
        if tools:
            print(f"DEBUG: Converting {len(tools)} tools to functions format")
            for i, tool in enumerate(tools):
                print(f"DEBUG: Tool {i}: {type(tool)} - {tool}")

                # Handle Agno Function objects directly
                if hasattr(tool, 'name') and hasattr(tool, 'entrypoint'):
                    # This is an Agno Function object - use directly
                    functions[tool.name] = tool
                    print(f"DEBUG: Added Function object: {tool.name}")

                # Handle Toolkit objects - extract Function objects from .functions
                elif hasattr(tool, 'functions') and hasattr(tool, 'name'):
                    # This is a Toolkit - extract all Function objects
                    for func_name, func_obj in tool.functions.items():
                        functions[func_name] = func_obj
                        print(f"DEBUG: Added Function from Toolkit: {func_name}")

                # Handle raw callables (from direct access like yf_tools.get_current_stock_price)
                elif callable(tool) and hasattr(tool, '__name__'):
                    # This is a raw callable - we need to find the corresponding Function object
                    # This case should be avoided, but handle it for backward compatibility
                    print(f"DEBUG: Warning - received raw callable: {tool.__name__}")
                    func_obj = Function.from_callable(tool)
                    functions[func_obj.name] = func_obj
                    print(f"DEBUG: Created Function from callable: {func_obj.name}")

                # Handle tool call format (for future compatibility)
                elif isinstance(tool, dict) and 'function' in tool:
                    func_name = tool['function'].get('name')
                    if func_name:
                        # Create a complete Function object with the actual entrypoint
                        func_dict = tool['function']

                        # Map function names to actual YFinance methods
                        yfinance_entrypoints = self._get_yfinance_entrypoints()

                        if func_name in yfinance_entrypoints:
                            # Create a proper Function object with entrypoint
                            func_obj = Function(
                                name=func_name,
                                description=func_dict.get('description', f"Tool: {func_name}"),
                                parameters=func_dict.get('parameters', {"type": "object", "properties": {}, "required": []}),
                                entrypoint=yfinance_entrypoints[func_name],
                                requires_confirmation=func_dict.get('requires_confirmation', False),
                                external_execution=func_dict.get('external_execution', False)
                            )
                            functions[func_name] = func_obj
                            print(f"DEBUG: Created complete Function object with entrypoint for: {func_name}")
                        else:
                            # Fallback to dict format
                            functions[func_name] = func_dict
                            print(f"DEBUG: Added Function from dict (fallback): {func_name}")
                else:
                    print(f"DEBUG: Unknown tool format: {tool}")
            print(f"DEBUG: Converted to {len(functions)} functions: {list(functions.keys())}")

        # If no tools, use standard response
        if not functions:
            print("DEBUG: No functions, using parent ainvoke")
            return await super().ainvoke(messages, response_format, None, tool_choice)

        # Step 1: Modify system message to include tool instructions
        print(f"DEBUG: Processing {len(functions)} functions")
        print(f"DEBUG: Input messages: {[msg.role if hasattr(msg, 'role') else type(msg) for msg in messages]}")
        modified_messages = self._inject_tool_instructions(messages, functions)
        print(f"DEBUG: Modified messages: {[msg.role if hasattr(msg, 'role') else type(msg) for msg in modified_messages]}")

        # Step 2: Convert any tool roles to assistant (Ollama compatibility)
        ollama_messages = []
        for msg in modified_messages:
            if msg.role == 'tool':
                # Convert tool role to assistant for Ollama compatibility
                ollama_messages.append(Message(role='assistant', content=msg.content))
                print("DEBUG: Converted tool message to assistant role")
            else:
                ollama_messages.append(msg)

        # Get initial response from GPT-OSS
        initial_response_raw = await super().ainvoke(ollama_messages, response_format, None, tool_choice)

        # Extract content from response
        initial_content = initial_response_raw.get('message', {}).get('content', '')

        # Step 3: Check for tool calls in the response
        if initial_content:
            print(f"DEBUG: Response content: {initial_content[:200]}...")

            tool_calls = self._extract_tool_calls_from_content(initial_content)

            if tool_calls:
                print(f"DEBUG: Found {len(tool_calls)} tool calls: {tool_calls}")
                print(f"DEBUG: Available functions: {list(functions.keys())}")

                # Execute tool calls
                tool_results = self._execute_tool_calls(tool_calls, functions)
                print(f"DEBUG: Tool execution results: {len(tool_results)} results")

                # Format results for final response
                tool_results_text = self._format_tool_results(tool_results)

                # Create follow-up prompt with tool results
                follow_up_content = f"Tool results:\n{tool_results_text}\n\nBased on these results, provide your final response:"

                # Get final response incorporating tool results  
                final_messages = modified_messages + [
                    Message(role='assistant', content=initial_content),
                    Message(role='user', content=follow_up_content)
                ]

                # Convert any tool roles to assistant for this call too
                final_ollama_messages = []
                for msg in final_messages:
                    if msg.role == 'tool':
                        final_ollama_messages.append(Message(role='assistant', content=msg.content))
                        print("DEBUG: Converted tool message to assistant role in final call")
                    else:
                        final_ollama_messages.append(msg)

                print("DEBUG: Getting final response with tool results")
                final_response_obj = await super().ainvoke(final_ollama_messages, response_format, None, tool_choice)

                # Convert to dict if it's a Pydantic object
                if hasattr(final_response_obj, 'model_dump'):
                    final_response_raw = final_response_obj.model_dump()
                elif hasattr(final_response_obj, 'dict'):
                    final_response_raw = final_response_obj.dict()
                else:
                    final_response_raw = dict(final_response_obj) if not isinstance(final_response_obj, dict) else final_response_obj

                # Add tool calls to response for Agno framework
                if 'message' not in final_response_raw:
                    final_response_raw['message'] = {}

                # Create tool calls in Agno format
                agno_tool_calls = []
                for i, (tool_call, result) in enumerate(zip(tool_calls, tool_results)):
                    agno_call = {
                        "id": f"call_{i}",
                        "type": "function", 
                        "function": {
                            "name": tool_call.get('name', ''),
                            "arguments": tool_call.get('arguments', {})
                        }
                    }
                    agno_tool_calls.append(agno_call)

                final_response_raw['tool_calls'] = agno_tool_calls

                return final_response_raw

        # No tool calls found, return initial response
        return initial_response_raw

    def _inject_tool_instructions(self, messages: List[Message], functions: dict) -> List[Message]:
        """Inject tool instructions into the system message."""
        modified_messages = []

        for message in messages:
            if message.role == "system":
                # Enhance system message with tool instructions
                enhanced_content = self._create_system_prompt_with_tools(message.content, functions)
                if self.debug:
                    logger.debug(f"Enhanced system message: {enhanced_content[:500]}...")
                modified_messages.append(Message(role="system", content=enhanced_content))
            else:
                modified_messages.append(message)

        # If no system message exists, create one with tools
        if not any(msg.role == "system" for msg in messages):
            tool_prompt = self._create_system_prompt_with_tools("You are a helpful assistant.", functions)
            if self.debug:
                logger.debug(f"Created system message with tools: {tool_prompt[:500]}...")
            modified_messages.insert(0, Message(role="system", content=tool_prompt))

        return modified_messages

    def _format_tool_results(self, tool_results: List[Dict[str, Any]]) -> str:
        """Format tool execution results for GPT-OSS to understand."""
        if not tool_results:
            return "No tool results."

        formatted = []
        for i, result in enumerate(tool_results, 1):
            tool_call = result.get('tool_call', {})
            tool_name = tool_call.get('name', 'unknown')

            formatted.append(f"Tool {i}: {tool_name}")

            if 'error' in result:
                formatted.append(f"  Error: {result['error']}")
            elif 'result' in result:
                formatted.append(f"  Result: {result['result']}")
            else:
                formatted.append("  No result")

            formatted.append("")  # Empty line

        return "\n".join(formatted)

    def _create_agno_tool_calls(self, tool_calls: List[Dict[str, Any]], tool_results: List[Dict[str, Any]]) -> List[Dict[str, Any]]:
        """Create Agno-compatible tool call objects."""
        agno_tool_calls = []

        for i, (tool_call, result) in enumerate(zip(tool_calls, tool_results)):
            agno_call = {
                "id": f"call_{i}",
                "type": "function",
                "function": {
                    "name": tool_call.get('name', ''),
                    "arguments": tool_call.get('arguments', {})
                }
            }
            agno_tool_calls.append(agno_call)

        return agno_tool_calls

    def get_request_params(self, tools: Optional[List[Dict[str, Any]]] = None) -> Dict[str, Any]:
        """Override to disable tools parameter completely for GPT-OSS."""
        # Never pass tools to Ollama for GPT-OSS since we handle them manually
        return super().get_request_params(tools=None)

